<span class="line"><span style="color:#E1E4E8;">  content </span><span style="color:#F97583;">=</span><span style="color:#E1E4E8;"> content </span><span style="color:#F97583;">||</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">this</span></span>
